query:
query v2ProductSearch($page: Int!, $pageSize: Int!, $keyword: String, $filters: [V2ProductSearchFilterInput]!, $sort: V2ProductSearchSortInput, $withInventoryStock: Boolean!, $categoryFilterBaseCategoryId: String) {
  v2ProductSearch(input: {page: $page, limit: $pageSize, keyword: $keyword, filters: $filters, sort: $sort}) {
    __typename
    ...productSearchResultMobile
  }
}

fragment productSearchResultMobile on V2ProductSearchResult {
  __typename
  totalCount
  products {
    __typename
    ...coreProductMobile
  }
  sorts {
    __typename
    ...productSearchSortResult
  }
  filters {
    __typename
    ...productSearchFilterResult
  }
}

fragment coreProductMobile on V2Product {
  __typename
  ...simpleProduct
  ... on V2SimpleProduct {
    inventoryStock @include(if: $withInventoryStock) {
      __typename
      ...inventoryStock
    }
    __typename
  }
  ... on V2ConfigurableProduct {
    ...configurableProductMobile
    __typename
  }
}

fragment simpleProduct on V2Product {
  __typename
  id
  sku
  name
  type
  urlKey
  createdAt
  brand {
    __typename
    ...brand
  }
  breadcrumbs {
    __typename
    ...breadcrumb
  }
  overlayImageUrl
  thumbnailUrl
  badge
  promotionTag
  shortDescription
  description
  flags
  mediaGallery {
    __typename
    ...media
  }
  seller {
    __typename
    ...seller
  }
  priceSummary {
    __typename
    ...priceSummary
  }
  purchaseLimit {
    __typename
    ...purchaseLimit
  }
  preorder {
    __typename
    ...preorder
  }
  promotions {
    __typename
    ...promotion
  }
  flashdeal {
    __typename
    ...flashDeal
  }
  reviews {
    __typename
    ...review
  }
  rating {
    __typename
    ...rating
  }
  shippingMethods
  paymentMethods
  installmentPlans {
    __typename
    ...installmentPlan
  }
  the1Redemption {
    __typename
    ...t1Redemption
  }
  labelOptions {
    __typename
    id
    label
  }
}

fragment brand on V2Brand {
  __typename
  id
  name
  urlKey
  logo
  imageUrl
  description
  contentCss
}

fragment breadcrumb on V2Breadcrumb {
  __typename
  id
  level
  title
  urlKey
}

fragment media on V2Media {
  __typename
  id
  type
  url
  title
}

fragment seller on V2Seller {
  __typename
  id
  name
}

fragment priceSummary on V2PriceSummary {
  __typename
  original
  final
  discount {
    __typename
    ...discount
  }
}

fragment discount on V2Discount {
  __typename
  amount
  percentage
  effectiveDateRange {
    __typename
    ...dateRange
  }
}

fragment dateRange on V2DateRange {
  __typename
  from
  to
}

fragment purchaseLimit on V2PurchaseLimit {
  __typename
  quantity {
    __typename
    ...amountRange
  }
}

fragment amountRange on V2AmountRange {
  __typename
  min
  max
}

fragment preorder on V2Preorder {
  __typename
  shippingDateTime
}

fragment promotion on V2Promotion {
  __typename
  id
  title
  description
  effectiveDateRange {
    __typename
    ...dateRange
  }
}

fragment flashDeal on V2Flashdeal {
  __typename
  effectiveDateRange {
    __typename
    ...dateRange
  }
  quantity {
    __typename
    ...flashDealQuantity
  }
}

fragment flashDealQuantity on V2FlashdealQuantity {
  __typename
  sale
  available
  sold
}

fragment review on V2Review {
  __typename
  id
  title
  detail
  imageUrls
  reviewer {
    __typename
    ...reviewer
  }
  rating
  createdAt
}

fragment reviewer on V2Reviewer {
  __typename
  name
  email
  province {
    __typename
    ...place
  }
}

fragment place on Place {
  __typename
  id
  name
}

fragment rating on V2Rating {
  __typename
  average
  totalVoteCount
}

fragment installmentPlan on V2InstallmentPlan {
  __typename
  id
  title
  period
  interestRate
  validDateRange {
    __typename
    ...dateRange
  }
  amountRange {
    __typename
    ...amountRange
  }
  bank {
    __typename
    ...bank
  }
}

fragment bank on V2Bank {
  __typename
  id
  name
  imageUrl
}

fragment t1Redemption on V2The1Redemption {
  __typename
  earnPoint
  redeemPoint
}

fragment inventoryStock on V2InventoryStock {
  __typename
  sku
  quantity
}

fragment configurableProductMobile on V2ConfigurableProduct {
  __typename
  priceRange {
    __typename
    ...priceRange
  }
  options {
    __typename
    ...configurableOption
  }
  children {
    __typename
    ...configurableProductChildMobile
  }
}

fragment priceRange on V2ConfigurableProductPriceRange {
  __typename
  original
  final
  discount {
    __typename
    ...discount
  }
}

fragment configurableOption on V2ConfigurableOption {
  __typename
  id
  label
  attributeCode
  values {
    __typename
    ...configurableOptionValue
  }
}

fragment configurableOptionValue on V2ConfigurableOptionValue {
  __typename
  id
  type
  label
  colorCode
  url
}

fragment configurableProductChildMobile on V2ConfigurableProductChild {
  __typename
  options {
    __typename
    ...configurableProductChildOption
  }
  product {
    __typename
    ...simpleProduct
    ... on V2SimpleProduct {
      inventoryStock @include(if: $withInventoryStock) {
        __typename
        ...inventoryStock
      }
      __typename
    }
  }
}

fragment configurableProductChildOption on V2ProductOption {
  __typename
  id
  label
  attributeCode
  value {
    __typename
    ...configurableOptionValue
  }
}

fragment productSearchSortResult on V2ProductSearchSortResult {
  __typename
  id
  label
  direction
}

fragment productSearchFilterResult on V2ProductSearchFilterResult {
  __typename
  id
  label
  ... on V2ProductSearchFilterNormalResult {
    ...productSearchFilterNormalResult
    __typename
  }
  ... on V2ProductSearchFilterCategoryResult {
    ...productSearchFilterCategoryResult
    __typename
  }
  ... on V2ProductSearchFilterRangeResult {
    ...productSearchFilterRangeResult
    __typename
  }
}

fragment productSearchFilterNormalResult on V2ProductSearchFilterNormalResult {
  __typename
  options {
    __typename
    ...productSearchFilterNormalResultOption
  }
}

fragment productSearchFilterNormalResultOption on V2ProductSearchFilterNormalResultOption {
  __typename
  id
  label
  productCount
}

fragment productSearchFilterCategoryResult on V2ProductSearchFilterCategoryResult {
  __typename
  options(baseCategoryId: $categoryFilterBaseCategoryId) {
    __typename
    ...productSearchFilterCategoryResultOption
    children {
      __typename
      ...productSearchFilterCategoryResultOption
      children {
        __typename
        ...productSearchFilterCategoryResultOption
        children {
          __typename
          ...productSearchFilterCategoryResultOption
        }
      }
    }
  }
}

fragment productSearchFilterCategoryResultOption on V2ProductSearchFilterCategoryResultOption {
  __typename
  id
  label
  productCount
  urlPath
  level
}

fragment productSearchFilterRangeResult on V2ProductSearchFilterRangeResult {
  __typename
  options {
    __typename
    ...productSearchFilterRangeResultOption
  }
}

fragment productSearchFilterRangeResultOption on V2ProductSearchFilterRangeResultOption {
  __typename
  id
  label
  value
}
